func maxProfit(prices []int, fee int) int {
n := len(prices)
cost, profit := prices[0]+fee, 0
for i := 1; i &lt; n; i++ {
if prices[i]+fee &lt; cost {
// 降低持仓成本
cost = prices[i] + fee
} else if prices[i] &gt; cost {
// 贪心，能赚就赚
