boolean[][] dp = new boolean[k + 1][k + 1];
dp[0][0] = true;
Arrays.sort(c); // 排序优化枚举效率
int s = 0, ms = 0;
for (int x : c) {
s += x;
ms = Math.min(s, k); // 子集元素和从当前最大和开始枚举
