// 不选择x，或者选择x放入子集1，或者选择x放入子集2
dp[j1][j2] |= (j1 &gt;= x &amp;&amp; dp[j1 - x][j2]) || (j2 &gt;= x &amp;&amp; dp[j1][j2 - x]);
}
}
}
int cnt = 0;
int[] ans = new int[k + 1];
for (int x = 0; x &lt;= k; x++) {
if (dp[x][k - x]) {
ans[cnt++] = x;
}
}
out.println(cnt);
