for ; n &gt; 1; n-- {
Fscan(in, &amp;v, &amp;w)
ug[v] = append(ug[v], w&lt;&lt;1)   // 最低位 0 表示正向
ug[w] = append(ug[w], v&lt;&lt;1|1) // 最低位 1 表示反向
}
// 第一遍DFS计算以节点1为根时，需要反向的边的数量
var dfs1 func(x, fa int) int
dfs1 = func(x, fa int) (c int) {
for _, y := range ug[x] {
if y&gt;&gt;1 != fa {
c += y&amp;1 + dfs1(y&gt;&gt;1, x)
}
}
return
}
min := dfs1(1, 0)
// 第二遍DFS通过换根DP计算每个节点为根时，需要反向的边的数量
var xs []int
var dfs func(x, fa, c int)
dfs = func(x, fa, c int) {
if c &lt; min {
xs, min = []int{x}, c
} else if c == min {
xs = append(xs, x)
}
for _, y := range ug[x] {
if y&gt;&gt;1 != fa {
// 如果x-&gt;y为正向，则以节点y为根时，需要反向的边的数量+1；如果为反向则-1
dfs(y&gt;&gt;1, x, c-(y&amp;1)&lt;&lt;1+1)
}
}
}
dfs(1, 0, min)
sort.Ints(xs)
Fprintln(out, min)
for _, x := range xs {
