q = q[1:]
for _, y := range ug[x] {
if dis[y] == -1 { // 第一次访问节点y，计算与节点o的最短距离，并入队
dis[y] = dis[x] + 1
q = append(q, x&lt;&lt;shift|y)
} else if y != f { // 第二次访问节点y，通过判断排除y-x-y这种不成环的访问情形
ans = min(ans, dis[x]+dis[y]+1)
}
}
}
}
if ans == math.MaxInt {
return -1
} else {
return ans
}
}
