Fscan(in, &amp;n, &amp;k, &amp;s)
r := k
if m := bits.Len32(uint32(n - k + 1)); m &lt; k {
r = m
}
l := k - r
mask := 1&lt;&lt;r - 1
has := make([]bool, 1&lt;&lt;r)
var l1, rt int
for _, b := range s[:l] {
l1 += int(b &amp; 1)
}
for _, b := range s[l:k] {
rt = rt&lt;&lt;1 | int(b&amp;1)
}
has[rt] = l1 == l
for i, b := range s[k:] {
l1 += int(s[i+l]&amp;1) - int(s[i]&amp;1)
rt = (rt&lt;&lt;1 | int(b&amp;1)) &amp; mask
if l1 == l {
has[rt] = true
}
}
rt = mask
for ; rt != -1 &amp;&amp; has[rt]; rt-- {
}
if rt == -1 {
Fprintln(out, "NO")
} else {
Fprintf(out, "YES\n%0*b\n", k, rt^mask)
}
}
}
</code></pre> <pre><code>import java.io.*;
