Arrays.sort(arr, (a, b) -&gt; Integer.compare(b[0], a[0]));
int[] ans = new int[n];
// 离散化树状数组计算前序有多少个区间右端点小于当前区间右端点
Arrays.sort(rs);
BIT bit = new BIT(n + 1);
for (int[] lri : arr) {
int x = Arrays.binarySearch(rs, lri[1]);
ans[lri[2]] = bit.query(x);
bit.update(x + 1, 1);
}
for (int c : ans) {
out.println(c);
}
out.flush();
out.close();
in.close();
}
