private static class BIT {
int n;
int[] arr;
BIT(int n) {
this.n = n;
this.arr = new int[n];
}
// 单点更新
void update(int i, int v) {
for (; i &lt; n; i += (i &amp; -i)) {
arr[i] += v;
}
}
// 前缀查询 [1, i]
int query(int i) {
int sum = 0;
for (; i &gt; 0; i &amp;= (i - 1)) {
